import React, { useEffect, useState, useCallback } from 'react';
import { FaCheckCircle, FaClock, FaMedal, FaYoutube, FaArrowRight } from 'react-icons/fa';
import axios from 'axios';

const getYouTubeVideoId = (url) => {
  try {
    const regExp = /^.*(youtu.be\/|v\/|u\/\w\/|embed\/|watch\?v=|&v=)([^#&?]*).*/;
    const match = url.match(regExp);
    return (match && match[2].length === 11) ? match[2] : null;
  } catch (error) {
    console.error('Error extracting YouTube video ID:', error);
    return null;
  }
};

const AssessmentResults = ({ submissions }) => {
  const [suggestedCourses, setSuggestedCourses] = useState([]);
  const [loading, setLoading] = useState(false);
  const [selectedCourse, setSelectedCourse] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);

  const handleOpenCourseModal = (course) => {
    setSelectedCourse(course);
    setIsModalOpen(true);
  };

  const handleCloseModal = () => {
    setIsModalOpen(false);
    setSelectedCourse(null);
  };

  const fetchSuggestedCourses = useCallback(async () => {
    try {
      setLoading(true);
      const token = localStorage.getItem('token');
      const response = await axios.get('http://localhost:5000/api/dashboard/recommendations', {
        headers: { Authorization: `Bearer ${token}` }
      });

      console.log('Recommendations response:', response.data);
      if (response.data.recommendations) {
        setSuggestedCourses(response.data.recommendations);
      }
    } catch (error) {
      console.error('Error fetching recommendations:', error);
      setSuggestedCourses([]);
    } finally {
      setLoading(false);
    }
  }, []);

  useEffect(() => {
    if (submissions && submissions.length > 0) {
      console.log('Triggering recommendations fetch for', submissions.length, 'submissions');
      fetchSuggestedCourses();
    }
  }, [fetchSuggestedCourses, submissions]);

  const formatDate = (dateString) => new Date(dateString).toLocaleString();

  const getStatusColor = (score) => {
    if (score >= 70) return 'text-green-600';
    if (score >= 50) return 'text-yellow-600';
    return 'text-red-600';
  };

  const getProgressBarColor = (score) => {
    if (score >= 70) return 'bg-green-500';
    if (score >= 50) return 'bg-yellow-500';
    return 'bg-red-500';
  };

  const getLevelBadgeStyle = (level) => {
    switch (level?.toLowerCase()) {
      case 'beginner': return 'bg-green-100 text-green-800';
      case 'intermediate': return 'bg-blue-100 text-blue-800';
      case 'expert': return 'bg-purple-100 text-purple-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  return (
    <div className="space-y-6">
      {/* ...Assessment History and Learning Recommendations sections (unchanged)... */}

      {isModalOpen && selectedCourse && (
        <div className="fixed inset-0 bg-black bg-opacity-30 flex items-center justify-center z-50">
          <div className="bg-white rounded-lg shadow-lg p-6 max-w-md w-full relative">
            <button
              onClick={handleCloseModal}
              className="absolute top-2 right-2 text-gray-400 hover:text-gray-600"
            >
              ✕
            </button>
            <h3 className="text-lg font-semibold text-gray-900 mb-2">{selectedCourse.title}</h3>
            <p className="text-sm text-gray-700 mb-4">{selectedCourse.description}</p>

            {selectedCourse.topics?.length > 0 && (
              <div className="mb-4 flex flex-wrap gap-2">
                {selectedCourse.topics.map((topic, i) => (
                  <span key={i} className="px-2 py-1 bg-gray-100 rounded-full text-xs text-gray-600">
                    {topic}
                  </span>
                ))}
              </div>
            )}

            <a
              href={selectedCourse.videoResources[0].url}
              target="_blank"
              rel="noopener noreferrer"
              className="block w-full text-center py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
            >
              View Course
            </a>
          </div>
        </div>
      )}
    </div>
  );
};

export default AssessmentResults;
