import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Login from './components/Login';
import Register from './components/Register';
import AdminDashboard from './components/AdminDashboard';
import StudentDashboard from './components/StudentDashboard';
import AssessmentTaking from './components/AssessmentTaking';
import PrivateRoute from './components/PrivateRoute';
import LearningPaths from './components/LearningPaths';
import StudentPathway from './components/StudentPathway';


const App = () => {
  return (
    <Router>
      <Routes>
        {/* Public Routes */}
        <Route path="pathway" element={<StudentPathway />} />
        <Route path="/pathway" element={<LearningPaths />} />
        <Route path="/login" element={<Login />} />
        <Route path="/register" element={<Register />} />
        <Route path="/forgot-password" element={<div>Forgot Password (Coming Soon)</div>} />

        {/* Protected Routes */}
        <Route 
          path="/admin-dashboard" 
          element={
            <PrivateRoute>
              <AdminDashboard />
            </PrivateRoute>
          } 
        />
        <Route 
          path="/student-dashboard" 
          element={
            <PrivateRoute>
              <StudentDashboard />
            </PrivateRoute>
          } 
        />
        <Route 
        path="/pathway" 
        element={
        <PrivateRoute>
          <LearningPaths />
        </PrivateRoute>
      } 
      />
        <Route 
          path="/assessment/:id" 
          element={
            <PrivateRoute>
              <AssessmentTaking />
            </PrivateRoute>
          } 
        />

        {/* Redirect root to login */}
        <Route path="/" element={<Navigate to="/login" />} />
        
        {/* Catch all other routes */}
        <Route path="*" element={<Navigate to="/login" />} />
      </Routes>
    </Router>
  );
};

export default App;
