import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { FaBook, FaCheckCircle, FaClock, FaUserGraduate, FaVideo } from 'react-icons/fa';
import { getStudentAssessments, getStudentStats, getStudentRecommendations } from '.api/student';

export default function StudentDashboard() {
  const navigate = useNavigate();
  const [assessments, setAssessments] = useState([]);
  const [stats, setStats] = useState({});
  const [recommendations, setRecommendations] = useState([]);

  useEffect(() => {
    async function fetchData() {
      const [assessmentsData, statsData, recommendationsData] = await Promise.all([
        getStudentAssessments(),
        getStudentStats(),
        getStudentRecommendations(),
      ]);
      setAssessments(assessmentsData);
      setStats(statsData);
      setRecommendations(recommendationsData);
    }
    fetchData();
  }, []);

  return (
    <div className="max-w-7xl mx-auto p-6 space-y-10">
      <h1 className="text-3xl font-bold text-gray-800">Welcome Back 👋</h1>

      <section className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-6">
        <StatCard icon={<FaBook />} label="Total Assessments" value={stats.totalAssessments} />
        <StatCard icon={<FaCheckCircle />} label="Completed" value={stats.completedAssessments} />
        <StatCard icon={<FaUserGraduate />} label="Average Score" value={`${stats.averageScore || 0}%`} />
        <StatCard icon={<FaClock />} label="Total Time Spent" value={`${stats.totalTime || 0} mins`} />
      </section>

      <section>
        <h2 className="text-2xl font-semibold text-gray-700 mb-4">Your Assessments</h2>
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
          {assessments.map((a) => (
            <AssessmentCard key={a.id} assessment={a} onClick={() => navigate(`/assessment/${a.id}`)} />
          ))}
        </div>
      </section>

      <section>
        <h2 className="text-2xl font-semibold text-gray-700 mb-4">Recommended for You</h2>
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
          {recommendations.map((r, i) => (
            <RecommendationCard key={i} resource={r} />
          ))}
        </div>
      </section>
    </div>
  );
}

function StatCard({ icon, label, value }) {
  return (
    <div className="bg-white rounded-2xl shadow-md p-6 flex items-center gap-4">
      <div className="p-3 bg-gradient-to-tr from-blue-100 to-blue-200 rounded-full text-blue-600">
        {icon}
      </div>
      <div>
        <p className="text-sm text-gray-500">{label}</p>
        <p className="text-xl font-bold text-gray-800">{value}</p>
      </div>
    </div>
  );
}

function AssessmentCard({ assessment, onClick }) {
  const { title, duration, level, questions, submission } = assessment;

  return (
    <div className="bg-white rounded-2xl shadow-md hover:shadow-lg transition-all duration-300 p-6 border border-gray-100">
      <div className="flex flex-col justify-between h-full">
        <div className="mb-4">
          <h3 className="text-xl font-semibold text-gray-800 mb-2">{title}</h3>
          <div className="flex flex-wrap items-center gap-3 text-sm text-gray-500">
            <span className={`px-3 py-1 rounded-full text-xs font-semibold capitalize ${
              level === 'beginner' ? 'bg-green-100 text-green-700' :
              level === 'intermediate' ? 'bg-blue-100 text-blue-700' :
              'bg-purple-100 text-purple-700'
            }`}>
              {level}
            </span>
            <span className="flex items-center gap-1">
              <FaClock className="text-gray-400" />
              {duration} mins
            </span>
            <span>{questions.length} questions</span>
          </div>
        </div>

        <button
          onClick={onClick}
          className={`w-full py-2 rounded-xl text-sm font-medium shadow-sm transition-colors ${
            submission ? 'bg-green-600 hover:bg-green-700' : 'bg-blue-600 hover:bg-blue-700'
          } text-white`}
        >
          {submission ? 'Retake' : 'Start'}
        </button>
      </div>
    </div>
  );
}

function RecommendationCard({ resource }) {
  const getIcon = (type) => {
    switch (type) {
      case 'video': return <FaVideo className="text-blue-600" />;
      case 'article': return <FaBook className="text-green-600" />;
      default: return <FaBook className="text-gray-600" />;
    }
  };

  return (
    <div className="bg-white rounded-2xl overflow-hidden shadow-sm border border-gray-100 transition hover:shadow-lg">
      <div className="p-5">
        <div className="flex items-center gap-3 mb-3 text-blue-600 font-semibold">
          {getIcon(resource.type)}
          <h3 className="text-lg text-gray-800">{resource.title}</h3>
        </div>
        <p className="text-gray-600 text-sm mb-4">{resource.description}</p>
        <div className="flex flex-wrap gap-2 text-xs text-gray-500 mb-4">
          {resource.topics?.slice(0, 3).map((topic, i) => (
            <span key={i} className="bg-gray-100 px-2 py-1 rounded-full">{topic}</span>
          ))}
          {resource.topics?.length > 3 && (
            <span className="bg-gray-100 px-2 py-1 rounded-full">
              +{resource.topics.length - 3} more
            </span>
          )}
        </div>
        <div className="flex justify-between items-center">
          <span className="text-sm text-gray-500 capitalize">Difficulty: {resource.difficulty}</span>
          <a
            href={resource.url}
            target="_blank"
            rel="noopener noreferrer"
            className="px-4 py-2 text-sm bg-blue-600 text-white rounded-xl hover:bg-blue-700"
          >
            Learn
          </a>
        </div>
      </div>
    </div>
  );
}
